mapper.select:
select month key, count(STARTCITY) value from DEPARTDETAIL where year = #{year}
and CARTYPE = #{cartype}
and ISMONTHCOUNT = 1 group by month order by month

select listagg(distinct CARID, ',') within group ( order by CARID desc) CARID, COLUMNNAME, round(sum(onemonth), 2) onemonth, round(sum(twomonth), 2) twomonth, round(sum(threemonth), 2) threemonth, round(sum(fourmonth), 2) fourmonth, round(sum(fivemonth), 2) fivemonth, round(sum(sixmonth), 2) sixmonth, round(sum(sevenmonth), 2) sevenmonth, round(sum(eightmonth), 2) eightmonth, round(sum(ninemonth), 2) ninemonth, round(sum(tenmonth), 2) tenmonth, round(sum(eleventmonth), 2) eleventmonth, round(sum(twelvemonth), 2) twelvemonth from INCOMESTATEMENT I where year = #{year}
and I.CARTYPE = #{cartype}
group by COLUMNNAME
