mapper.select:
select max(CARTYPE) cartype, carid, listagg(columnname, ',') within group ( order by columnname desc) columnname, sum(threemonth )threemonth, sum(fourmonth )fourmonth, sum(fivemonth) fivemonth, sum(sixmonth) sixmonth, sum(sevenmonth )sevenmonth, sum(eightmonth) eightmonth, sum(ninemonth) ninemonth, sum(tenmonth )tenmonth, sum(eleventmonth )eleventmonth, sum(twelvemonth )twelvemonth, sum(onemonth ) onemonth, sum(twomonth) twomonth from ( select max(CARTYPE) cartype, columnname, listagg(CARID, ',') within group ( order by CARID desc) carid, sum(THREEMONTH) threemonth, sum(FOURMONTH) fourmonth, sum(FIVEMONTH) fivemonth, sum(SIXMONTH) sixmonth, sum(SEVENMONTH) sevenmonth, sum(EIGHTMONTH) eightmonth, sum(NINEMONTH) ninemonth, sum(TENMONTH) tenmonth, sum(ELEVENTMONTH) eleventmonth, sum(TWELVEMONTH) twelvemonth, sum(ONEMONTH) onemonth, sum(TWOMONTH) twomonth from INCOMESTATEMENT I where I.year =#{year}
and I.CARTYPE = #{carType}
and COLUMNNAME in
#{item}
group by COLUMNNAME ) a group by cartype, carid

select max(CARTYPE) cartype, carid, listagg(columnname, ',') within group ( order by columnname desc) columnname, round(sum(threemonth/10000) , 2) threemonth, round(sum(fourmonth/10000) , 2) fourmonth, round(sum(fivemonth/10000) , 2) fivemonth, round(sum(sixmonth/10000) , 2) sixmonth, round(sum(sevenmonth/10000) , 2) sevenmonth, round(sum(eightmonth/10000), 2) eightmonth, round(sum(ninemonth/10000), 2) ninemonth, round(sum(tenmonth/10000), 2) tenmonth, round(sum(eleventmonth/10000), 2) eleventmonth, round(sum(twelvemonth/10000), 2) twelvemonth, round(sum(onemonth/10000) , 2) onemonth, round(sum(twomonth/10000) , 2) twomonth from ( select max(CARTYPE) cartype, columnname, listagg(CARID, ',') within group ( order by CARID desc) carid, sum(THREEMONTH) threemonth, sum(FOURMONTH) fourmonth, sum(FIVEMONTH) fivemonth, sum(SIXMONTH) sixmonth, sum(SEVENMONTH) sevenmonth, sum(EIGHTMONTH) eightmonth, sum(NINEMONTH) ninemonth, sum(TENMONTH) tenmonth, sum(ELEVENTMONTH) eleventmonth, sum(TWELVEMONTH) twelvemonth, sum(ONEMONTH) onemonth, sum(TWOMONTH) twomonth from INCOMESTATEMENT I where I.CARTYPE = #{carType} and I.year =#{year} and COLUMNNAME in
#{item}
group by COLUMNNAME ) a group by cartype, carid
