mapper.select:
select * from ( select CARTYPE, month month, sum(kilo) / 10000 kilosum, count(SHEETID) runcount, round(sum(kilo) / count(SHEETID), 10) / 10000 avg from DEPARTDETAIL D where YEAR = #{year}
and CARTYPE = #{carType}
and d.ISMONTHCOUNT = 1 and d.ISKILOSUM = 1 group by month, CARTYPE ) a order by month

select max(CARTYPE) cartype, columnname, listagg(CARID, ',') within group ( order by CARID desc) carid, sum(THREEMONTH) / 10000 threemonth, sum(FOURMONTH) / 10000 fourmonth, sum(FIVEMONTH) / 10000 fivemonth, sum(SIXMONTH) / 10000 sixmonth, sum(SEVENMONTH) / 10000 sevenmonth, sum(EIGHTMONTH) / 10000 eightmonth, sum(NINEMONTH) / 10000 ninemonth, sum(TENMONTH) / 10000 tenmonth, sum(ELEVENTMONTH) / 10000 eleventmonth, sum(TWELVEMONTH) / 10000 twelvemonth, sum(ONEMONTH) / 10000 onemonth, sum(TWOMONTH) / 10000 twomonth from INCOMESTATEMENT I where year = #{year}
and CARTYPE = #{carType}
group by COLUMNNAME

select max(CARTYPE) cartype, carid, listagg(columnname, ',') within group ( order by columnname desc) columnname, sum(threemonth )threemonth, sum(fourmonth )fourmonth, sum(fivemonth) fivemonth, sum(sixmonth) sixmonth, sum(sevenmonth )sevenmonth, sum(eightmonth) eightmonth, sum(ninemonth) ninemonth, sum(tenmonth )tenmonth, sum(eleventmonth )eleventmonth, sum(twelvemonth )twelvemonth, sum(onemonth ) onemonth, sum(twomonth) twomonth from ( select max(CARTYPE) cartype, columnname, listagg(CARID, ',') within group ( order by CARID desc) carid, sum(THREEMONTH) threemonth, sum(FOURMONTH) fourmonth, sum(FIVEMONTH) fivemonth, sum(SIXMONTH) sixmonth, sum(SEVENMONTH) sevenmonth, sum(EIGHTMONTH) eightmonth, sum(NINEMONTH) ninemonth, sum(TENMONTH) tenmonth, sum(ELEVENTMONTH) eleventmonth, sum(TWELVEMONTH) twelvemonth, sum(ONEMONTH) onemonth, sum(TWOMONTH) twomonth from INCOMESTATEMENT I where I.year =#{year}
and I.CARTYPE = #{carType}
and COLUMNNAME in
#{item}
group by COLUMNNAME ) a group by cartype, carid

select max(CARTYPE) cartype, carid, listagg(columnname, ',') within group ( order by columnname desc) columnname, sum(threemonth/10000) threemonth, sum(fourmonth/10000) fourmonth, sum(fivemonth/10000) fivemonth, sum(sixmonth/10000) sixmonth, sum(sevenmonth/10000) sevenmonth, sum(eightmonth/10000) eightmonth, sum(ninemonth/10000) ninemonth, sum(tenmonth/10000) tenmonth, sum(eleventmonth/10000) eleventmonth, sum(twelvemonth/10000) twelvemonth, sum(onemonth/10000) onemonth, sum(twomonth/10000) twomonth from ( select max(CARTYPE) cartype, columnname, listagg(CARID, ',') within group ( order by CARID desc) carid, sum(THREEMONTH) threemonth, sum(FOURMONTH) fourmonth, sum(FIVEMONTH) fivemonth, sum(SIXMONTH) sixmonth, sum(SEVENMONTH) sevenmonth, sum(EIGHTMONTH) eightmonth, sum(NINEMONTH) ninemonth, sum(TENMONTH) tenmonth, sum(ELEVENTMONTH) eleventmonth, sum(TWELVEMONTH) twelvemonth, sum(ONEMONTH) onemonth, sum(TWOMONTH) twomonth from INCOMESTATEMENT I where I.year =#{year}
and CARTYPE = #{carType}
and COLUMNNAME in
#{item}
group by COLUMNNAME ) a group by cartype, carid
